  def gen_slug(), do: generate(:text)
  def gen_slug(:svg), do: generate(:text) |> url_to_svg()

  def gen_name(:random) do
    gender = Enum.random([:male, :female])
    gen_name(gender)
  end

  def gen_name(:male), do: random(:lastname) <> " " <> random(:male_name)
  def gen_name("male"), do: random(:lastname) <> " " <> random(:male_name)
  def gen_name(:female), do: random(:lastname) <> " " <> random(:female_name)
  def gen_name("female"), do: random(:lastname) <> " " <> random(:female_name)

  def random(:noun), do: Enum.random(@noun)
  def random(:adjs), do: Enum.random(@adjs)
  def random(:lastname), do: Enum.random(@lastname)
  def random(:male_name), do: Enum.random(@male_name)
  def random(:female_name), do: Enum.random(@female_name)

  defp generate(:text) do
    one = random(:noun)
    two = random(:adjs)
    three = random(:noun)
    four = random(:adjs)

    case Enum.random(0..1) do
      0 ->
        "#{one}-#{two}-#{three}"

      1 ->
        "#{two}-#{three}-#{four}"
    end
  end

  def url_to_svg(url) do
    url
    |> EQRCode.encode()
    |> EQRCode.svg(width: 256, color: <<18, 18, 18>>)
  end
end
